import React from 'react';
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register the required components with Chart.js
ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const BarChart = ({ data, onClick }) => {
  const chartData = {
    labels: data.map((item) => item.disease),
    datasets: [
      {
        label: 'Probability (%)',
        data: data.map((item) => item.probability * 100),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    layout: {
      padding: {
        bottom: 40, // Adds extra space below the chart
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        min: 0,
        max: 100,
        title: {
          display: true,
          text: 'Probability (%)',
          font: {
            size: 14,
            weight: 'bold',
          },

        },
      },
      x: {
        title: {
          display: true,
          text: 'Diseases',
          font: {
            size: 14,
            weight: 'bold',
          },
          padding: {
            top: 10, // Adds space between x-axis title and labels
          },
        },
        ticks: {
          // Ensure x-axis labels are not angled
          autoSkip: false, // Prevent skipping labels
          maxRotation: 0,  // Prevent rotation of labels
          minRotation: 0,  // Prevent rotation of labels
        },
      },
    },
    plugins: {
      legend: {
        display: false, // Hide legend if unnecessary
      },
    },
    onClick: (event, elements) => {
      if (elements.length > 0) {
        const index = elements[0].index;
        const selectedDisease = data[index];
        onClick(selectedDisease); // Pass clicked data back to parent
      }
    },
  };

  const styles = {
    chartContainer: {
      // width: '100%',
      // maxWidth: '800px',
      // height: '420px',
      // margin: '0px auto',
      // padding: '20px',
      // boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      // borderRadius: '8px',
      // backgroundColor: '#fff',
      width: '100%',
      maxWidth: '800px',
      height: '500px', // Adjusted height for proper alignment
      margin: '0 auto', // Centers the component horizontally
      padding: '20px',
      display: 'flex', // Enables flexbox
      justifyContent: 'center', // Centers content horizontally
      alignItems: 'center', // Centers content vertically
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      borderRadius: '8px',
      backgroundColor: '#fff',
    },
    title: {
      textAlign: 'center',
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '10px',
     
    },
  };

  return (
    <div style={styles.chartContainer}>
      <h3 style={styles.title}>Disease Probability Chart</h3>
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default BarChart;
