import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Widget } from 'react-chat-widget';
import 'react-chat-widget/lib/styles.css';
import avatar from '../assets/chatbot-avatar.png'; // Ensure this path is correct

const Chatbot = () => {
  const [messages, setMessages] = useState([]); // state to store messages
  const [isFirstMessage, setIsFirstMessage] = useState(true); // to track if it's the first message

  // Function to handle new user messages
  const handleNewUserMessage = (message) => {
    console.log(`New message incoming: ${message}`);

    // Add the user's message to the chat (for internal state)
    setMessages((prevMessages) => [
      ...prevMessages,
      { user: true, text: message }, // push user message to state
    ]);

    // Send the message to the Flask backend
    axios
      .get('http://localhost:5002/get', { params: { msg: message } })
      .then((response) => {
        const botMessage = response.data;
        setMessages((prevMessages) => [
          ...prevMessages,
          { user: false, text: botMessage }, // push bot response to state
        ]);
      })
      .catch((error) => {
        console.error('Error getting response from the bot:', error);
        setMessages((prevMessages) => [
          ...prevMessages,
          { user: false, text: 'Sorry, something went wrong.' }, // Error handling
        ]);
      });
  };

  // Effect hook for initializing first messages
  useEffect(() => {
    if (isFirstMessage) {
      // Initially set the messages
      setMessages([
        { user: false, text: "Hello, my name is RoboDoc, and I will be happy to help diagnose your disease." },
        { user: false, text: "To start, we need to ask some basic questions. Type 'OK' to continue." },
      ]);
      setIsFirstMessage(false); // Set this flag to false so this effect doesn't run again
    }
  }, [isFirstMessage]);

  // Render the chat widget with the necessary props
  return (
    <div>
      <Widget
        handleNewUserMessage={handleNewUserMessage}
        profileAvatar={avatar}
        title="AI Healthcare Assistant"
        subtitle="How can I help you today?"
      />
    </div>
  );
};

export default Chatbot;
