import React from 'react';

const DetailedView = ({ disease, resultType }) => {
  if (!resultType) return null;

  const renderContent = () => {
    switch (resultType) {
      case 'Disease':
        return <p>{disease.disease}</p>;
      case 'Description':
        return <p>{disease.description}</p>;
      case 'Precaution':
        return <ul>{disease.precautions.map((item, idx) => <li key={idx}>{item}</li>)}</ul>;
      case 'Medications':
        return <ul>{disease.medications[0].split(', ').map((item, idx) => <li key={idx}>{item}</li>)}</ul>;
      case 'Workouts':
        return <ul>{disease.workout.map((item, idx) => <li key={idx}>{item}</li>)}</ul>;
      case 'Diets':
        return <ul>{disease.diets[0].split(', ').map((item, idx) => <li key={idx}>{item}</li>)}</ul>;
      default:
        return null;
    }
  };

  return (
    <div style={styles.detailedView}>
      {renderContent()}
    </div>
  );
};

const styles = {
  detailedView: {
    marginTop: '20px',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  },
};

export default DetailedView;
