import React from 'react';
import { Navigate } from 'react-router-dom';
import { useUser } from './UserContext';  // Importing the useUser hook to get user details

const AdminRoute = ({ element }) => {
  const { userDetails } = useUser();  // Access user details from context

  // If the user is an admin, render the element (AdminDashboard)
  // Otherwise, redirect to the home page
  return userDetails?.isAdmin ? element : <Navigate to="/" />;
};

export default AdminRoute;
