import React, { useState, useEffect } from "react";
import { TextField, Button, Select, MenuItem, FormControl, InputLabel, Typography, Box } from "@mui/material";
import axios from "axios";

const ContactDoctor = () => {
  const [specialization, setSpecialization] = useState("");
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [formData, setFormData] = useState({
    userName: "",
    gender: "",
    age: "",
    symptoms: "",
    doctorId: "",
  });

  useEffect(() => {
    // Fetch all doctors
    axios.get("http://localhost:5000/doctor/viewdoctor")
      .then((res) => setDoctors(res.data))
      .catch((err) => console.error("Error fetching doctors:", err));
  }, []);

  const handleSpecializationChange = (e) => {
    const value = e.target.value;
    setSpecialization(value);
    // Filter doctors based on specialization
    setFilteredDoctors(doctors.filter((doctor) => doctor.specialize.toLowerCase().includes(value.toLowerCase())));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://localhost:5000/api/submitRequest", formData)
      .then(() => {
        alert("Request submitted successfully!");
        setFormData({ userName: "", gender: "", age: "", symptoms: "", doctorId: "" });
      })
      .catch((err) => console.error("Error submitting request:", err));
  };

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Contact a Doctor
      </Typography>

      {/* Specialization Search */}
      <FormControl fullWidth margin="normal">
        <InputLabel>Search by Specialization</InputLabel>
        <Select value={specialization} onChange={handleSpecializationChange}>
          <MenuItem value="">All</MenuItem>
          {doctors
            .map((doc) => doc.specialize)
            .filter((value, index, self) => self.indexOf(value) === index) // Get unique specializations
            .map((spec, idx) => (
              <MenuItem key={idx} value={spec}>
                {spec}
              </MenuItem>
            ))}
        </Select>
      </FormControl>

      {/* List of Doctors */}
      <Box mt={2}>
        <Typography variant="h6" gutterBottom>
          {filteredDoctors.length > 0 ? "Available Doctors:" : "No doctors found"}
        </Typography>
        {filteredDoctors.map((doctor) => (
          <Box key={doctor._id} border={1} p={2} mb={1} borderRadius={2}>
            <Typography variant="body1">
              <strong>Name:</strong> {doctor.name}
            </Typography>
            <Typography variant="body1">
              <strong>Specialization:</strong> {doctor.specialize}
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={() => setFormData({ ...formData, doctorId: doctor._id })}
            >
              Choose
            </Button>
          </Box>
        ))}
      </Box>

      {/* Contact Form */}
      {formData.doctorId && (
        <Box mt={4}>
          <Typography variant="h6" gutterBottom>
            Fill out the form to contact the doctor
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              margin="normal"
              name="userName"
              label="Your Name"
              value={formData.userName}
              onChange={handleInputChange}
              required
            />
            <FormControl fullWidth margin="normal">
              <InputLabel>Gender</InputLabel>
              <Select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                required
              >
                <MenuItem value="Male">Male</MenuItem>
                <MenuItem value="Female">Female</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            <TextField
              fullWidth
              margin="normal"
              name="age"
              label="Age"
              type="number"
              value={formData.age}
              onChange={handleInputChange}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              name="symptoms"
              label="Symptoms / Disease Description"
              multiline
              rows={4}
              value={formData.symptoms}
              onChange={handleInputChange}
              required
            />
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Submit Request
            </Button>
          </form>
        </Box>
      )}
    </Box>
  );
};

export default ContactDoctor;
