import React from 'react';

const ResultsButtons = ({ onClick }) => {
  const resultTypes = [
    'Disease',
    'Description',
    'Precaution',
    'Medications',
    'Workouts',
    'Diets',
  ];

  return (
    <div style={styles.buttonGroup}>
      {resultTypes.map((type) => (
        <button key={type} onClick={() => onClick(type)} style={styles.resultButton}>
          {type}
        </button>
      ))}
    </div>
  );
};

const styles = {
  buttonGroup: {
    display: 'flex',
    flexWrap: 'wrap',         // Ensure buttons wrap when necessary
    gap: '10px',
    justifyContent: 'center', // Center buttons horizontally
    marginTop: '20px',
  },
  resultButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',      // Added left-right padding for better button width
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
    width: '100%',             // Make buttons full width on small screens
    maxWidth: '200px',         // Set a max width for larger screens
    textAlign: 'center',      // Center text inside the button
  },
};

export default ResultsButtons;
