import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext';
import SymptomDropdown from './SymptomDropdown';
import axios from 'axios';
import Footer from '../components/Footer';
import BarChart from './BarChart'; // Corrected BarChart component
import ResultsButtons from './ResultsButtons';
import DetailedView from './DetailedView';
import Loader from './Loader';

const Home = () => {
  const { isLoggedIn } = useUser();
  const navigate = useNavigate();
  const [symptoms, setSymptoms] = useState('');
  const [results, setResults] = useState([]);
  const [selectedDisease, setSelectedDisease] = useState(null);
  const [selectedResultType, setSelectedResultType] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isLoggedIn) {
      alert('Please log in to predict.');
      navigate('/login');
      return;
    }

    if (!symptoms.trim()) {
      alert('Please enter symptoms before predicting.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:5000/api/general', {
        symptoms,
      });
      setResults(response.data);
      setSelectedDisease(null);
      setSelectedResultType(null);
    } catch (error) {
      console.error('Error fetching prediction:', error);
      setError('An error occurred while fetching predictions.');
    } finally {
      setLoading(false);
    }
  };

  const handleDiseaseSelection = (disease) => {
    setSelectedDisease(disease);
    setSelectedResultType(null);
  };

  const handleResultTypeSelection = (resultType) => {
    if (!selectedDisease) {
      alert('Please select a disease from the chart first.');
      return;
    }
    setSelectedResultType(resultType);
  };

  return (
    <div style={styles.pageContainer}>
      <div style={styles.contentWrapper}>
        <div style={styles.mainSection}>
          <h1 style={styles.header}>Health Care Center</h1>
          <div style={styles.card}>
            <SymptomDropdown setSymptoms={setSymptoms} />
            <input
              type="text"
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
              placeholder="Enter symptoms..."
              style={styles.input}
            />
            <button onClick={handleSubmit} style={styles.button} disabled={loading}>
              {loading ? <Loader /> : 'Predict'}
            </button>
          </div>
        </div>

        <div style={styles.resultsSection}>
          <h2 style={styles.resultsHeader}>Disease Probabilities</h2>
          {results.length > 0 && (
            <BarChart data={results} onClick={handleDiseaseSelection} />
          )}
          {error && <p style={styles.errorText}>{error}</p>}

          {selectedDisease && (
            <>
              <ResultsButtons onClick={handleResultTypeSelection} />
              <DetailedView
                disease={selectedDisease}
                resultType={selectedResultType}
              />
            </>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

const styles = {
  // Styles here remain unchanged

  pageContainer: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  contentWrapper: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
  },
  mainSection: {
    backgroundColor: '#f7f7f7',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    width: '100%',
    maxWidth: '800px',
  },
  header: { fontSize: '2rem', marginBottom: '20px' },
  card: { marginBottom: '20px' },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #ddd',
    borderRadius: '4px',
  },
  button: { width: '100%', padding: '10px', borderRadius: '4px' },
  resultsSection: { marginTop: '30px', textAlign: 'center', width: '100%' },
  resultsHeader: { fontSize: '1.5rem', marginBottom: '20px' },
  errorText: { color: 'red' },
};

export default Home;
