import React from 'react';
import { Widget } from 'react-chat-widget';
import 'react-chat-widget/lib/styles.css';
import avatar from '../assets/chatbot-avatar.png'; // Make sure you have a chatbot avatar image in the assets folder

const Chatbot = () => {
  // Function to handle new user messages
  const handleNewUserMessage = (message) => {
    console.log(`New message incoming: ${message}`);

    // Simulate sending the message to an API or backend and getting a response
    // This can be replaced by an actual backend call
    const botMessage = `You said: "${message}". How can I assist you further?`;

    // Simulate the bot responding
    setTimeout(() => {
      // This is where you send the response back to the chat widget
      window.dispatchEvent(
        new CustomEvent('react-chat-widget-send', {
          detail: {
            text: botMessage,
            from: 'bot',
          },
        })
      );
    }, 1000);
  };

  return (
    <Widget
      handleNewUserMessage={handleNewUserMessage}
      profileAvatar={avatar} // Avatar image for the chatbot
      title="AI Healthcare Assistant"
      subtitle="How can I help you today?"
    />
  );
};

export default Chatbot;
